import React, { useEffect } from "react";
import { useStateContext } from "../../utils/GlobalState";
import { Container, Row, Col } from "../Grid";
import { List, ListItem } from "../List";

const SearchResults = () => {

    const [state, dispatch] = useStateContext();

    const addSaved = async (id) => {
        try {
            const data = state.posts.id;
            console.log(state.posts.id);
            const response = await API.saveBook(data);
            console.log(response);
        } catch (err) {
            console.log(err)
        };
    };

    return (

// TODO - add button to save book to db

        state && state.posts && state.posts.length ? (
                state.posts.map(result =>
                    <ListItem key={result.id}>
                        <Container>
                            <Row>
                                <Col size="md-3">
                                    <img src={result.volumeInfo.imageLinks.smallThumbnail} />
                                </Col>
                                <Col size="md-9">
                                    <Container>
                                        <Row>
                                            <strong>
                                                {result.volumeInfo.title} by {result.volumeInfo.authors.join(",")}
                                            </strong>
                                            <button className="btn" onClick={ () => addSaved(result.id)}>
                                                ❤️ Add to Saved
                                            </button>
                                        </Row>
                                        {result.volumeInfo.description ? (
                                            <Row>
                                                {result.volumeInfo.description.substring(0,250) /*only load 250 characters*/} 
                                            </Row>
                                            ) : (
                                            <Row>
                                                No description available.    
                                            </Row>
                                        )}
                                    </Container>
                                </Col>
                            </Row>
                        </Container>
                    </ListItem>
                )


        ) : (
            <h3>Search for a book to see results.</h3>
        )
    );
};

export default SearchResults;
