import API from "../../utils/API";
import { Container, Row, Col } from "../Grid";
import { List, ListItem } from "../List";

const BookCard = () => {

    var books;

    // TODO - add useEffect to get books (API.findAll) on load ([])
    const getBooks = async () => {
        const response = await API.getBooks();
        books = response.data;
        return books;
    };

    // books are not loaded by the time the page tries to render
    useEffect(() => {
        getBooks();
        console.log(books);
    }, []);

    return (

// TODO - remove state and get data from db (done)
