        books ? (
            books.map(result =>
                <ListItem key={result._id}>
                    <Container>
                        <Row>
                            <Col size="md-3">
                                <img src={result.imageLinks.smallThumbnail} />
                            </Col>
                            <Col size="md-9">
                                <Container>
                                    <Row>
                                        <strong>
                                            {result.title} by {result.author}
                                        </strong>
                                    </Row>
                                    {result.description ? (
                                        <Row>
                                            {result.description.substring(0,250) /*only load 250 characters*/} 
                                        </Row>
                                        ) : (
                                        <Row>
                                            No description available.    
                                        </Row>
                                    )}
                                </Container>
                            </Col>
                        </Row>
                    </Container>
                </ListItem>
            )


    ) : (
        <h3>No books have been saved.</h3>
    )

